	/**
	 * makeSphere() - create a Sphere object
	 */
	private void makeSphere() {

		for (int i = 0; i < sphereElements.length - 2; i += 3) {

			// calculate the base indices of the three vertices
			int point1 = 3 * sphereElements[i]; // slots 0, 1, 2
			int point2 = 3 * sphereElements[i + 1]; // slots 3, 4, 5
			int point3 = 3 * sphereElements[i + 2]; // slots 6, 7, 8

			int normal1 = 3 * sphereNormalIndices[i];
			int normal2 = 3 * sphereNormalIndices[i + 1];
			int normal3 = 3 * sphereNormalIndices[i + 2];

			canvas.addTriangleWithNorms(new Vertex(sphereVertices[point1 + 0], sphereVertices[point1 + 1], sphereVertices[point1 + 2]),
					new Normal(sphereNormals[normal1 + 0], sphereNormals[normal1 + 1], sphereNormals[normal1 + 2]),

			new Vertex(sphereVertices[point2 + 0], sphereVertices[point2 + 1], sphereVertices[point2 + 2]),
			new Normal(sphereNormals[normal2 + 0], sphereNormals[normal2 + 1], sphereNormals[normal2 + 2]),

			new Vertex(sphereVertices[point3 + 0], sphereVertices[point3 + 1], sphereVertices[point3 + 2]),
			new Normal(sphereNormals[normal3 + 0], sphereNormals[normal3 + 1], sphereNormals[normal3 + 2]));
		}
	}

	
